import {
  Flex,
  Box,
  FormControl,
  FormLabel,
  Input,
  InputGroup,
  HStack,
  InputRightElement,
  Stack,
  Button,
  Heading,
  Text,
  useColorModeValue,
  Link,
  Checkbox,
  useToast,
} from "@chakra-ui/react";
import { useState } from "react";
import { ViewIcon, ViewOffIcon } from "@chakra-ui/icons";
import { useNavigate } from "react-router-dom";
import axios from "axios";

export default function SignupCard() {
  const [showPassword, setShowPassword] = useState(false);
  const [formData, setFormData] = useState({
    phoneNumber: "",
    password: "",
    retypePassword: "",
    fullName: "",
    dateOfBirth: "",
    address: "",
    isAccepted: false,
  });
  const [showError, setError] = useState("");
  const navigate = useNavigate();
  const toast = useToast();

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: type === "checkbox" ? checked : value,
    }));
  };

  const checkPasswordsMatch = () => {
    if (formData.retypePassword !== formData.password) {
      setError("Passwords do not match");
    } else {
      setError("");
    }
  };

  const handleSignUp = async () => {
    if (formData.password.length < 3) {
      setError("Password must be at least 3 characters long");
      return;
    }
    if (!formData.isAccepted) {
      setError("You must accept the terms and conditions");
      return;
    }

    try {
      await axios.post("https://3mftky-8080.csb.app/auth/signup", formData);
      console.log("Registered successfully!");
      navigate("/login");
      toast({
        title: "Registered successfully!",
        description: "Login to access the site.",
        status: "success",
        duration: 2000,
        isClosable: true,
      });
    } catch (error) {
      setError(error.response.data.errors[0]);
      toast({
        title: "Unable to create account",
        description: "Please try again with the required fields.",
        status: "error",
        duration: 2000,
        isClosable: true,
      });
    }
  };

  return (
    <Flex
      minH={"100vh"}
      align={"center"}
      justify={"center"}
      bg={useColorModeValue("gray.50", "gray.800")}
    >
      <Stack spacing={8} mx={"auto"} maxW={"lg"} py={12} px={6}>
        <Stack align={"center"}>
          <Heading fontSize={"4xl"} textAlign={"center"}>
            Sign Up
          </Heading>
        </Stack>
        <Box
          rounded={"lg"}
          bg={useColorModeValue("white", "gray.700")}
          boxShadow={"lg"}
          p={8}
        >
          <Stack spacing={4}>
            <FormControl id="phoneNumber" isRequired>
              <FormLabel>Phone Number</FormLabel>
              <Input
                type="text"
                name="phoneNumber"
                value={formData.phoneNumber}
                onChange={handleChange}
              />
            </FormControl>
            <FormControl id="password" isRequired>
              <FormLabel>Password</FormLabel>
              <InputGroup>
                <Input
                  type={showPassword ? "text" : "password"}
                  name="password"
                  value={formData.password}
                  onChange={handleChange}
                />
                <InputRightElement h={"full"}>
                  <Button
                    variant={"ghost"}
                    onClick={() => setShowPassword(!showPassword)}
                  >
                    {showPassword ? <ViewIcon /> : <ViewOffIcon />}
                  </Button>
                </InputRightElement>
              </InputGroup>
              {formData.password.length < 3 && (
                <Text color="red">Password must be at least 3 characters</Text>
              )}
            </FormControl>
            <FormControl id="retypePassword" isRequired>
              <FormLabel>Retype Password</FormLabel>
              <Input
                type="password"
                name="retypePassword"
                value={formData.retypePassword}
                onChange={(e) => {
                  handleChange(e);
                  checkPasswordsMatch();
                }}
              />
              {formData.retypePassword.length < 3 && (
                <Text color="red">
                  Retype Password must be at least 3 characters
                </Text>
              )}
              {showError && <Text color="red">{showError}</Text>}
            </FormControl>
            <FormControl id="fullName" isRequired>
              <FormLabel>Full Name</FormLabel>
              <Input
                type="text"
                name="fullName"
                value={formData.fullName}
                onChange={handleChange}
              />
            </FormControl>
            <FormControl id="dateOfBirth" isRequired>
              <FormLabel>Date of Birth</FormLabel>
              <Input
                type="date"
                name="dateOfBirth"
                value={formData.dateOfBirth}
                onChange={handleChange}
              />
            </FormControl>
            <FormControl id="address">
              <FormLabel>Address</FormLabel>
              <Input
                type="text"
                name="address"
                value={formData.address}
                onChange={handleChange}
              />
            </FormControl>
            <FormControl id="isAccepted" isRequired>
              <Checkbox
                name="isAccepted"
                isChecked={formData.isAccepted}
                onChange={handleChange}
              >
                I accept the terms and conditions
              </Checkbox>
            </FormControl>
            <Stack spacing={10} pt={2}>
              <Button
                loadingText="Submitting"
                size="lg"
                bg={"red.400"}
                color={"white"}
                _hover={{
                  bg: "red.300",
                }}
                onClick={handleSignUp}
              >
                Sign Up
              </Button>
            </Stack>
            <Stack pt={6}>
              <Text align={"center"}>
                Already a user?{" "}
                <Link color={"blue.400"} onClick={() => navigate("/login")}>
                  Login
                </Link>
              </Text>
            </Stack>
          </Stack>
        </Box>
      </Stack>
    </Flex>
  );
}
